catalog.catalogExpression:
{
  diagram: import('@funcdraw/diagram');
  source: diagram.box([-8, 0], [10, 6], { label: 'Source' });
  target: diagram.box([8, 0], [10, 6], { label: 'Target' });
  queue: diagram.box([-8, -9], [9, 5], { label: 'Queue'; fill: '#fef3c7'; stroke: '#b45309'; strokeWidth: 0.25; labelSize: 2.1; });
  connector: diagram.connector.line({ start: source.attachments.right; end: target.attachments.left; });
  sourceTop: source.attachments.top.point;
  targetTop: target.attachments.top.point;
  elbowY: sourceTop[1] - 4;
  topRoute: diagram.connector.polyline({
    start: source.attachments.top;
    end: target.attachments.top;
    via: [
      [sourceTop[0], elbowY],
      [targetTop[0], elbowY]
    ];
    style: { stroke: '#22c55e'; width: 0.3; dash: [0.6, 0.4]; };
    arrow: { stroke: '#15803d'; fill: '#22c55e'; headLength: 0.9; baseWidth: 0.65; };
  });
  queueRight: queue.attachments.right.point;
  targetBottom: target.attachments.bottom.point;
  elbowX: queueRight[0] + 6;
  elbowY2: targetBottom[1] + 3;
  bottomRoute: diagram.connector.polyline({
    start: queue.attachments.right;
    end: target.attachments.bottom;
    via: [
      [elbowX, queueRight[1]],
      [elbowX, elbowY2],
      [targetBottom[0], elbowY2]
    ];
    style: { stroke: '#a855f7'; width: 0.3; dash: [0.4, 0.4]; };
    arrow: { stroke: '#7c3aed'; fill: '#a855f7'; headLength: 0.8; baseWidth: 0.55; };
    startArrow: { stroke: '#7c3aed'; fill: '#a855f7'; headLength: 0.8; baseWidth: 0.55; };
  });
  eval [source.graphics, target.graphics, queue.graphics, connector.graphics, topRoute.graphics, bottomRoute.graphics];
}
